    str += "\nWalked the user through downloading the driver from the OKI website.";
if (jCheckBox4.isSelected())
    str += "\nWalked the user through installing the driver from the OKI website.";
if(jCheckBox5.isSelected())
    str += "\nWalked the user through the 'have disk' method of driver install.";
