    str += "\nWalked through removing the tray(s) from the unit.";
if(jCheckBox33.isSelected())
    str += "\nWalked through cleaning the rollers in the unit.";
if(jCheckBox6.isSelected())
    str += "\nWalked the user through removing the toner(s).";
if(jCheckBox7.isSelected())
    str += "\nWalked the user through removing the drum(s).";
if(jCheckBox8.isSelected())
    str += "\nWalked the user through removing the belt.";
if(jCheckBox9.isSelected())
    str += "\nWalked the user through removing the transfer assy.";
if(jCheckBox10.isSelected())
    str += "\nWalked the user through removing the fuser.";
if(jCheckBox11.isSelected())
    str += "\nWalked the user through reseating the LED head cable.";
if(jCheckBox12.isSelected())   
    str += "\nWalked the user through cleaning the belt contacts.";
if(jCheckBox13.isSelected())
    str += "\nWalked the user through cleaning the transfer assy contacts.";
if(jCheckBox14.isSelected())
    str += "\nWalked the user through cleaning the drum contacts.";
if(jCheckBox15.isSelected())
    str += "\nWalked the user through cleaning the toner contacts.";
if(jCheckBox16.isSelected())
    str += "\nWalked the user through cleaning the LED head cable contacts.";
if(jCheckBox17.isSelected())
    str += "\nWalked the user through cleaning the LED head.";
if(jCheckBox18.isSelected())
    str += "\nWalked the user through cleaning the toner sensors.";
if(jCheckBox19.isSelected())
    str += "\nWalked through printing an ID check pattern.";
if(jCheckBox20.isSelected())
    str += "\nWalked through enabling the ID check pattern";
if(jCheckBox37.isSelected())
    str += "\nWalked the end user through printing a test page.";
if(jCheckBox38.isSelected())
    str += "\n The test print came out perfectly.";
if(jCheckBox39.isSelected())
    str += "\n The print quality issue remains.";
